const data = $input.first().json;

console.log('Webhook recebido:', JSON.stringify(data));

if (data.event !== 'message_created' || data.message_type !== 'incoming') {
  return [{ json: { skip: true, reason: 'not_incoming' } }];
}

const msg = (data.content || '').toLowerCase().trim();
const convId = data.conversation?.id;
const senderName = data.sender?.name || 'Cliente';

if (!convId) {
  return [{ json: { skip: true, reason: 'no_conversation_id' } }];
}

let response = 'Desculpe, nao entendi. Digite MENU para ver as opcoes.';

if (/^(oi|ola|bom dia|boa tarde|boa noite|hello|hi|hey)/.test(msg)) {
  response = 'Ola! Bem-vindo ao TikTrend Finder!\n\n1 - Comparar precos\n2 - Ver ofertas\n3 - Falar com atendente\n\nDigite o numero da opcao!';
} else if (msg === '1' || msg.includes('preco') || msg.includes('comparar')) {
  response = 'Comparacao de Precos\n\nMe envie o nome do produto que voce quer comparar!\n\nExemplos: iPhone 15, Geladeira, Notebook';
} else if (msg === '2' || msg.includes('oferta')) {
  response = 'Ofertas do Dia\n\niPhone 14 - R$ 4.299 (12% OFF)\nNotebook Dell - R$ 3.199 (8% OFF)\nAirPods Pro - R$ 1.599 (15% OFF)';
} else if (msg === '3' || msg.includes('atendente') || msg.includes('humano')) {
  response = 'Atendimento Humano\n\nEstou transferindo para um atendente...\n\nTempo estimado: 2 minutos';
} else if (msg.includes('menu') || msg.includes('ajuda') || msg.includes('help')) {
  response = 'Menu Principal\n\n1 - Comparar precos\n2 - Ver ofertas\n3 - Falar com atendente\n\nDigite o numero!';
} else if (/^(obrigad|valeu|thanks)/.test(msg)) {
  response = 'Por nada! Estou aqui para ajudar!';
}

return [{
  json: {
    skip: false,
    conversationId: convId,
    response: response
  }
}];
